var Game = require('../Game.js');

module.exports = (function() {

  // Fill with a solid color
  function FillLayer()
  {
    this.color = 'black';
  }

  FillLayer.prototype.update = function(dt, time)
  {
    if (this.color)
      Game.instance.screen.fill(this.color);
  };

  return FillLayer;

})();
